import{openDB}from"idb";import CONFIG from"../global/config";const{DATABASE_NAME,DATABASE_VERSION,OBJECT_STORE_NAME}=CONFIG,dbPromise=openDB(DATABASE_NAME,DATABASE_VERSION,{upgrade(t){t.createObjectStore(OBJECT_STORE_NAME,{keyPath:"id"})}}),favoriteRestaurant={getFavoriteRestaurant:async t=>(await dbPromise).get(OBJECT_STORE_NAME,t),getAllFavoriteRestaurant:async()=>(await dbPromise).getAll(OBJECT_STORE_NAME),putFavoriteRestaurant:async t=>(await dbPromise).put(OBJECT_STORE_NAME,t),deleteFavoriteRestaurant:async t=>(await dbPromise).delete(OBJECT_STORE_NAME,t)};export default favoriteRestaurant;